mapper.select:
SELECT
user_id
FROM users 
WHERE name =#{name}
AND phone = #{phone}

  	SELECT *
  	FROM (	SELECT inner_table.* , ROWNUM AS row_seq
  					FROM		(	SELECT  *
										FROM users
										
											
												
									 				user_id = #{searchKeyword}
												
												
									 				name = #{searchKeyword}
												
											
										
										ORDER BY user_id ) inner_table
					WHERE ROWNUM <= #{endRowNum} )
	WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

  	SELECT COUNT(*)
  	FROM(	SELECT *
					FROM users
					
						
							
					 			user_id = #{searchKeyword}
							
							
					 		name = #{searchKeyword}
							
						
					 ) countTable

SELECT
password
FROM users 
WHERE user_id = #{userId}
AND name =#{name}
AND phone = #{phone}

mapper.update:
UPDATE users
password = #{password}, birth = #{birth}, address = #{address}, phone = #{phone}, profile_img = #{profileImg:VARCHAR}, interest_no1 = #{interestNo1}, interest_no2 = #{interestNo2:CHAR}, interest_no3 = #{interestNo3:CHAR}
WHERE user_id=#{userId}
